import React, { Component } from 'react';
import './App.css'
import TaskForm from './components/TaskForm'
import Controll from './components/Controll'
import TaskList from './components/TaskList'
import {connect} from 'react-redux'
import * as actions from './actions/index'
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            taskEditing: null,
            Filter: {
                Name: '',
                Status: -1
            },
            Keyword: '',
            Sort: 0
        };
    }



    addTask = () => {

        // if(this.state.taskEditing){
        //     this.setState({
        //         isDisplayForm: true,
        //         taskEditing: null
        //     })
        // }else{
        //     this.setState({
        //         isDisplayForm: !this.state.isDisplayForm
        //     })
        // }

        this.props.onToggleForm();

    }

    onShowForm = () => {
        this.setState({
            isDisplayForm: true
        })
    }

    findTask = (ID) => {
        var { tasks } = this.state;

        var index = -1;
        for (var i = 0; i < tasks.length; i++) {
            if (tasks[i].ID === ID) {
                index = i;
                break;
            }
        }

        return index;
    }

    updateItem = (ID) => {
        var { tasks } = this.state;

        var index = this.findTask(ID);

        if (index !== -1) {
            this.setState({
                taskEditing: tasks[index]
            })

            this.onShowForm();

            localStorage.setItem("tasks", JSON.stringify(tasks));
        }
    }

    filterList = (filterName, filterStatus)=>{
        this.setState({
            Filter: {
                Name: filterName.toLowerCase(),
                Status: filterStatus
            }
        })

    }

    searchList = (txtSearch)=>{
        this.setState({
            Keyword: txtSearch
        })
    }

    sortBy = (type) =>{
        this.setState({
            Sort: type
        })
    }

    render() {
        var {isDisplayForm} = this.props;
        var {Filter, Keyword, Sort } = this.state;

        var elmTaskForm = isDisplayForm === true ? <TaskForm 
            task={this.state.taskEditing} /> : '';
        // if(Filter){
        //     tasks = tasks.filter((task)=>{
        //         return task.Name.toLowerCase().indexOf(Filter.Name) !== -1;
        //     })
        //     tasks = tasks.filter((task)=>{
        //         if(Filter.Status == -1)
        //         {
        //             return task;
        //         }
        //         else{
        //             return task.Status === (Filter.Status == 1? true: false);
        //         }
        //     })
        // }

        // if(Keyword !== ""){
        //     tasks = tasks.filter((task)=>{
        //         return task.Name.toLowerCase().indexOf(Keyword) !== -1;
        //     })
        // }

        // if(Sort !== 0){
        //     tasks.sort((a,b)=>{
        //         if(a.Name.toLowerCase() > b.Name.toLowerCase()) return Sort;
        //         else if(a.Name.toLowerCase() < b.Name.toLowerCase()) return -Sort;
        //         else return 0;
        //     })
        // }

        return (
            <div className="container">
                <div className="text-center">
                    <h1>Quản Lý Công Việc</h1>
                    <hr />
                </div>
                <div className="row">
                    <div className={isDisplayForm === true ? "col-xs-4 col-sm-4 col-md-4 col-lg-4" : ""}>
                        {elmTaskForm}
                    </div>
                    <div className={isDisplayForm === true ? "col-xs-8 col-sm-8 col-md-8 col-lg-8" : "col-xs-12 col-sm-12 col-md-12 col-lg-12"}>
                        <button type="button" className="btn btn-primary" onClick={this.addTask}>
                            <span className="fa fa-plus mr-5"></span>&nbsp;Thêm Công Việc
                        </button>&nbsp;
                        <div className="row mt-15">
                            <Controll searchList = {this.searchList} sortBy = {this.sortBy}/>
                        </div>
                        <div className="row mt-15">
                            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                                <TaskList
                                    changeStatus={this.changeStatus}
                                    deleteTask={this.deleteTask}
                                    updateItem={this.updateItem} 
                                    filterList={this.filterList}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

const mapStateToProps = state =>{
    return {
        isDisplayForm: state.isDisplayForm
    }
}

const mapDispatchToProps = (dispatch, props) =>{
    return {
        onToggleForm: ()=>{
            dispatch(actions.toggleForm());
        },
        onCloseForm: ()=>{
            dispatch(actions.closeForm());
        }
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
