import * as types from '../constants/actionType'

var initialState = {
    name: '',
    status: -1
};

var myReducer = (filter = initialState, action)=>{
    
    switch(action.type){
        case types.FILTER_TABLE:
            filter = {
                name: action.filter.name,
                status: action.filter.status
            }
            return filter
        default:
            return filter
    }
}


export default myReducer;
